mapper.select:
select * from B_USER where STATUS = 1

  and USERNAME = #{username,jdbcType=VARCHAR}

order by CREATETIME desc

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into B_USER (USERNAME, PASSWORD, MOBILE, 
  STATUS, CREATETIME,UPDATETIME, REMARK
  )
values (#{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, #{mobile,jdbcType=VARCHAR}, 
  #{status,jdbcType=INTEGER}, #{createtime,jdbcType=TIMESTAMP}, #{updatetime,jdbcType=TIMESTAMP}, #{remark,jdbcType=VARCHAR}
  )
